import React from 'react'

import configValues from './config.js';

import TableR from './components/TableR.js';
import TableComponent from './components/TableComponent.js';

import HomeworkClub from './components/HomeworkClub.js';

import CollectionForm from './components/CollectionForm.js';
import DescriptionText from './components/DescriptionText.js';
import Selector from './components/Selector.js';
import CallbackButton from './components/CallbackButton.js';
import HomebridingTable from './components/HomebridingTable.js';
import DatePicker from './components/DatePicker.js';
import LoginMenu from './components/LoginMenu.js';
import ParentView from "./components/ParentView.js";
import Nav from "./components/Nav.js";

// React libraries
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

//// **** INITIALIZATIONS **** ////
// sets up the uniqid hashing function for use with component id's
var uniqid = require('uniqid');

// reference values from config.js
var students = configValues.students
var times = configValues.times
var classes = configValues.classes

// headers for each type of table
var hwcHeaders = configValues.homeworkClubHeaders
var detentionHeaders = []



function getFormattedDate(date) {
    var year = date.getFullYear();

    var month = (1 + date.getMonth()).toString();
    month = month.length > 1 ? month : '0' + month;

    var day = date.getDate().toString();
    day = day.length > 1 ? day : '0' + day;

    return month + '/' + day + '/' + year;
}



class App extends React.Component {
    state = {
        homeworkEntries: [],
        dueDate: "",
        descriptionText: "",
        condition: "",
        loginState: ""
    }

    componentDidMount() {
        this.setState({
            dueDate: getFormattedDate(new Date()),
            loginState: <LoginMenu
                callbackFunction={(event) => this.conditionalCheck(event)}
                students={students}
            />
        })
    }

    onClickAddHomeworkCard() {
        // TODO: refactor these uncontrolled components
        var qstudentName = document.querySelector('#studentNames').value
        var qclassSection = document.querySelector('#classSection').value

        var qReferenceIndex = uniqid()

        this.setState(prevState => ({
            homeworkEntries: [...prevState.homeworkEntries,
            <TableComponent
                id={uniqid()}
                referenceIndex={qReferenceIndex}
                studentName={qstudentName}
                selectorValue={qclassSection}
                description={this.state.descriptionText}
                dueDate={this.state.dueDate}
                callbackRef={(event) => this.onClickRemoveCard(event)}
            />
            ]
        }))
    }

    onClickRemoveCard(event) {
        this.setState(
            {
                homeworkEntries: this.state.homeworkEntries.filter(function (entry) {
                    return entry.props.referenceIndex !== event.target.id
                })
            }
        );
    }

    conditionalCheck() {
        this.setState({ condition: document.querySelector("#userName").value })

        // TODO: resolve condition not updating bug!
        if (document.querySelector("#userName").value === "admin") {
            this.setState({ loginState: <HomebridingTable students={students} tableLegend={"Homebriding 2020"} /> })
        } else {
            this.setState({ loginState: <ParentView /> })
        }
    }

    render() {
        return (
            <div className="App">

                <Router>

                    <Nav />

                    <Switch>
                        <Route path="/detentioncenter">
                            Detention Center
                        </Route>

                        <Route path="/homeworkclub">
                            Homework Club
                        </Route>

                        <Route path="/homebriding">
                            Homebriding
                        </Route>

                        <Route path="/supporttickets" >
                            <h1>Coming soon!</h1>
                        </Route>

                        <Route path="/guides" >
                            <h1>Coming soon!</h1>
                        </Route>

                        <Route path="/procedures" >
                            <h1>Coming soon!</h1>
                        </Route>
                    </Switch>

                </Router>




            </div >
        )
    }
}

export default App
