import React from 'react'

import LoginMenu from './components/LoginMenu.js';
import ParentView from "./components/ParentView.js";

// React libraries
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

//// **** INITIALIZATIONS **** ////
// sets up the uniqid hashing function for use with component id's
var uniqid = require('uniqid');


// TODO: add a link to the 'home' route from here so that
// users land on / and can't leave in the form of a conditional
// rendering function (call ParentView on success with StudentID passed in)

// goal: parents start in login menu, upon success something allows them into the
// parent view with the StudentID as a prop

class ParentClient extends React.Component {
    state = { condition: "", loginState: "", parent: {} }

    componentDidMount() {
        this.setState({
            loginState:
                <LoginMenu
                    callbackButton={() => this.authCheck()}
                    callbackOnChange={(event) => this.updateLoginState(event)}
                />
        })

    }

    /* componentDidUpdate(prevProps, prevState) {
        if (prevState.condition != this.state.condition) {
            console.log(this.state.parent)
        }
    } */

    // updates the state on keystroke change
    updateLoginState(event) {
        // TODO: figure out why state is one letter behind!
        this.setState({ condition: event.target.value })
    }

    // TODO: fname needs to be a username collected from the user!
    authCheck() {
        fetch('/parents', {
            method: 'POST',
            headers: new Headers({ 'Content-Type': 'application/json' }),
            body: JSON.stringify({ fname: this.state.condition })
        })
            .then(res => res.json())
            .then(parent => {
                if (parent != false) {
                    this.setState({ parent })
                }
            })
            .then(() => this.conditionalCheck())
    }



    // performs auth check
    conditionalCheck() {
        // conditional should be checking for a specific result and if `false` returns alert that the username/password failed
        if (this.state.parent != false) {
            // TODO: pass student property of found parent!
            this.state.loginState = <ParentView studentID="5e8e39d60bc7d2a215cc28c7" />
        } else {
            // TODO: add alert that result failed here
            console.log(this.state.parent)
        }
        // TODO: if parent exists in DB, proceed to parent view, else stop

    }

    render() {
        return (
            <div className="App">
                <Router>
                    <Switch>
                        <Route path="/" >
                            {this.state.loginState}
                        </Route>
                    </Switch>
                </Router>
            </div >
        )
    }
}

export default ParentClient
